function addVehicles(vehicleList){
  var vehicleGroup = svg.selectAll("g.vehicle-group")
    .data(vehicleList)
    .join(
      enter => {
        enter.append("text")
                .text(function(d) {
                  return d.id})
                .attr("width", 100)
                .attr("x", function(d,i){
                  return 20 + i*40;
                }) // Set the x-coordinate
                .attr("y", 450)
                .attr("text-anchor", "middle")
                .attr("font-size", 5);
        enter.append("text")
                .text(function(d) {
                  return d.currentStatus})
                .attr("width", 100)
                .attr("x", function(d,i){
                  return 20 + i*40;
                }) // Set the x-coordinate
                .attr("y", 460)
                .attr("text-anchor", "middle")
                .attr("font-size", 5)
                .attr("class", "current-status");
      },
      update => {
        update.attr("class", "updated");
      },
      exit => {exit.remove();}

    );
}

export default Vehicles;
