import { useState, useEffect } from "react";
import * as d3 from "d3";



var backendHost = "localhost:8080/";
var controllerEndpoint = "mbta/v1/livemap";
var vehiclePositionSub = "/vehicle/location/Green-E";
var mbtaStopsEndpoint = "/stops/line/Green-E";

function Vehicles() {    console.log("#80");
    const[event, setEvent] = useState("hi");
    const [vehicleList, setVehicleList] = useState([]);

 useEffect(() => {
      console.log(vehicleList);
      return addVehicles(vehicleList);
  }, [vehicleList]);

    //Load vehicles below
    useEffect(() => {
      const svg = d3.select("svg");

      const evtSource = new EventSource("http://" + backendHost + controllerEndpoint + vehiclePositionSub);
      evtSource.onmessage = (data) => {
        var eventData = JSON.parse(data.data);
        var vehicleData = eventData.vehicleData;
        if(eventData.event == "reset"){
          console.log("reset");
          setVehicleList(vehicleData);
        }
        if(eventData.event == "update"){
          console.log("update");
          if(!!vehicleList.find((vehicle) => vehicle.id === vehicleData[0].id)){
            setVehicleList((prevList) => [...prevList, vehicleData[0]]);
          }
          else {
            console.log("update .. ", vehicleData[0]);
            setVehicleList((prevList) =>
              prevList.map((vehicle) => {
                if (vehicle.id === vehicleData[0].id) {
                  return {...vehicle, ...vehicleData[0]};
                }
                return vehicle;
             })
            );
          }
        }//

      };
      evtSource.onerror = function (error) {
        console.error('EventSource error:', error);
      };
      //close out event subscription on close of component
      return () => {
        evtSource.close();
      };
    },[]);

}


function addVehicles(vehicleList){
  console.log("add vehicles .", vehicleList);
        const svg = d3.select("svg");

   var vehicleGroup = svg
    .selectAll("g.vehicle-group")
    .data(vehicleList, (d) => d.id)
    .join(
      (enter) => {
        var groupEnter = enter
          .append("g")
          .attr("class", "vehicle-group")
          .attr("transform", (d, i) => `translate(${10 + i * 40}, 0)`);

        groupEnter
          .append("rect")
          .attr("x", 0)
          .attr("y", 400)
          .attr("width", 10)
          .attr("height", 10)
          .attr("class", "vehicle")
          .attr("id", (d) => d.id);

        groupEnter
          .append("text")
          .text((d) => d.id)
          .attr("width", 100)
          .attr("x", 0)
          .attr("y", 450)
          .attr("text-anchor", "middle")
          .attr("font-size", 5);

        groupEnter
          .append("text")
          .text((d) => d.currentStatus)
          .attr("width", 100)
          .attr("x", 0)
          .attr("y", 460)
          .attr("text-anchor", "middle")
          .attr("font-size", 5)
          .attr("class", "current-status");
      },
      (update) => {
        update.attr("class", "updated");
      },
      (exit) => {
          exit.selectAll(".current-status") // Select the text element within the exit selection
            .transition() // Apply a transition for smooth removal
            .duration(500) // Set the duration of the transition
            .style("opacity", 0) // Fade out the text element
            .remove(); // Remove the text element

          exit.selectAll(".vehicle") // Select the rect element within the exit selection
            .transition() // Apply a transition for smooth removal
            .duration(500) // Set the duration of the transition
            .style("opacity", 0) // Fade out the rect element
            .remove(); // Remove the rect element

          exit.remove(); // Remove the entire vehicle-group element
      } 
    );
}

export default Vehicles;
