              .attr("cx", 0)
              .attr("cy", 0)
              .attr("r", 10)
              .attr("fill", "red");

             stopGroup.append("text")
                .text(function(d) {
                  console.log(d);
                  return d[Object.keys(d)[0]].name;})
                .attr("width", 100)
                .attr("x", 0) // Set the x-coordinate
                .attr("y", 20)
                .attr("text-anchor", "middle");

                console.log("hellohi")
        }
      }
    xhr.onerror = function (error){
      console.error(error);
    };
    xhr.send();
    console.log("sent");
}
