import Chart from 'chart.js';
import React, { useEffect } from 'react';

function Map() {
    useEffect(() => {
    const coordinates = [
      { lat: 42.39674, long: -71.12181 },
      { lat: 42.3884, long: -71.119149 }
    ]
    const minX = -180; // Minimum value for X (longitude)
    const maxX = 180; // Maximum value for X (longitude)
    const minY = -90; // Minimum value for Y (latitude)
    const maxY = 90; // Maximum value for Y (latitude)
    const chart = new Chart(canvas, {
      type: "scatter",
      data: {
        datasets: [
          {
            label: "Latitude and Longitude",
            data: coordinates.map(({ lat, long }) => ({ x: long, y: lat })),
            pointBackgroundColor: "rgba(75, 192, 192, 1)", // Customize dot color
            pointRadius: 6, // Customize dot size
          },
        ],
      },
      options: {
        scales: {
          x: {
            type: "linear",
            position: "bottom",
            title: {
              display: true,
              text: "Longitude",
            },
            min: minX, // Set the minimum value for X (longitude)
            max: maxX, // Set the maximum value for X (longitude)
          },
          y: {
            type: "linear",
            position: "left",
            title: {
              display: true,
              text: "Latitude",
            },
            min: minY, // Set the minimum value for Y (latitude)
            max: maxY, // Set the maximum value for Y (latitude)
          },
        },
      },
    });
  // Cleanup chart on component unmount
    return () => {
      chart.destroy();
    };
  }, []); // Empty dependency array ensures the effect runs only once

  return <canvas id="myChart" />;
}

export default Map;
