import { Line, Chart } from 'react-chartjs-2';
import { useState, useEffect } from "react";
import * as d3 from "d3";



var backendHost = "localhost:8080/";
var controllerEndpoint = "mbta/v1/livemap";
var vehiclePositionSub = "/vehicle/location/Red";
function Map() {
    const bostonBoundaries = {
      "westernBoundary" : -71.191,
      "easternBoundary" : -70.746,
      "southernBoundary" : 42.184,
      "northernBoundary" : 42.402
    };

    function createSVGMap(){
      d3.select("svg");
    }

    const[event, setEvent] = useState("hi");
    //Load stops here
    //Load lines here

    //Load vehicles below
    useEffect(() => {
      const evtSource = new EventSource("http://" + backendHost + controllerEndpoint + vehiclePositionSub);
      evtSource.onmessage = (data) => {
        setEvent(data.data);
      };
      evtSource.onerror = function (error) {
        console.error('EventSource error:', error);
      };
      //close out event subscription on close of component
    },[]);


      return (
        <div className="Map">
          <svg height="1000" width="1000"></svg>
          {createSVGMap()}
          <h1>Recent Event is {event}</h1>
        </div> 
      );
}

export default Map;
