function addVehicles(vehicleList){
  console.log("add vehicles .", vehicleList);
   var vehicleGroup = svg
    .selectAll("g.vehicle-group")
    .data(vehicleList, (d) => d.id)
    .join(
      (enter) => {
        var groupEnter = enter
          .append("g")
          .attr("class", "vehicle-group")
          .attr("transform", (d, i) => `translate(${10 + i * 40}, 0)`);

        groupEnter
          .append("rect")
          .attr("x", 0)
          .attr("y", 400)
          .attr("width", 10)
          .attr("height", 10)
          .attr("class", "vehicle")
          .attr("id", (d) => d.id);

        groupEnter
          .append("text")
          .text((d) => d.id)
          .attr("width", 100)
          .attr("x", 0)
          .attr("y", 450)
          .attr("text-anchor", "middle")
          .attr("font-size", 5);

        groupEnter
          .append("text")
          .text((d) => d.currentStatus)
          .attr("width", 100)
          .attr("x", 0)
          .attr("y", 460)
          .attr("text-anchor", "middle")
          .attr("font-size", 5)
          .attr("class", "current-status");
      },
      (update) => {
        update.select("rect.vehicle").attr("class", "updated");
      },
      (exit) => {
        exit.remove();
      }
    );
}

export default Vehicles;
