import { useState, useEffect } from "react";
import * as d3 from "d3";


var splitData = require('./split_data.json');
console.log("splitData : ", splitData);



var backendHost = "localhost:8080/";
var controllerEndpoint = "mbta/v1/livemap";
var vehiclePositionSub = "/vehicle/location/Green-E";
var mbtaStopsEndpoint = "/stops/line/Green-E";
const svg = d3.select("svg");

function Vehicles() {    console.log("#80");
    const[event, setEvent] = useState("hi");
    const [vehicleList, setVehicleList] = useState([]);


    //Load vehicles below
    useEffect(() => {
      const evtSource = new EventSource("http://" + backendHost + controllerEndpoint + vehiclePositionSub);
      evtSource.onmessage = (data) => {
        var eventData = JSON.parse(data.data);
        var vehicleData = eventData.vehicleData;
        if(eventData.event == "reset"){
          console.log("reset");
          setVehicleList(vehicleData);
          addVehicles(vehicleList);
        }
        // if(eventData.event == "update"){
        //   console.log("update");
        //   if(!!vehicleList.find((vehicle) => vehicle.id === vehicleData[0].id)){
        //     setVehicleList((prevList) => [...prevList, vehicleData[0]]);
        //   }
        //   else {  
        //     setVehicleList((prevList) =>
        //       prevList.map((vehicle) => {
        //         if (vehicle.id === vehicleData[0].id) {
        //           return { ...vehicle.currentStatus, ...vehicleData.currentStatus };
        //         }
        //         return vehicle;
        //      })
        //     );
        //   }
        // }
      };
      evtSource.onerror = function (error) {
        console.error('EventSource error:', error);
      };
      //close out event subscription on close of component
      //remove elements
      return () => {
        evtSource.close();
        svg.remove();
      };
    },[]);


    // useEffect(() => {
    // var vehicleGroups = svg.selectAll("g.vehicle-group");
    // // Update
    // vehicleGroups
    //   .merge(vehicleGroups);

    // vehicleGroups.select("rect.vehicle").attr("id", (d) => d.id);
    // vehicleGroups.select("text").text((d) => d.id);
    // vehicleGroups.select("text.current-status").text((d) => d.currentStatus);

    //  }, [vehicleList]);
}


function addVehicles(vehicleList){
  var svg = d3.select("svg");
  var vehicleGroup = svg.selectAll("g.vehicle-group")
    .data(vehicleList)
    .enter();

  vehicleGroup.append("rect")
  .attr("x",function(d,i){
    return 10 + i*40;
  })
  .attr("y",400)
  .attr("width",10)
  .attr("height",10)
  .attr("class", "vehicle")
  .attr("id", d => {return d.id});

  vehicleGroup.append("text")
                .text(function(d) {
                  return d.id})
                .attr("width", 100)
                .attr("x", function(d,i){
                  return 20 + i*40;
                }) // Set the x-coordinate
                .attr("y", 450)
                .attr("text-anchor", "middle")
                .attr("font-size", 5);
  vehicleGroup.append("text")
                .text(function(d) {
                  return d.currentStatus})
                .attr("width", 100)
                .attr("x", function(d,i){
                  return 20 + i*40;
                }) // Set the x-coordinate
                .attr("y", 460)
                .attr("text-anchor", "middle")
                .attr("font-size", 5);

}
export default Vehicles;
