import { useState, useEffect, useRef } from "react";
import * as d3 from "d3";
import Draw from'./draw.js';
import Vehicles from'./Vehicles.js';

var backendHost = "localhost:8080/";
var controllerEndpoint = "mbta/v1/livemap";
var vehiclePositionSub = "/vehicle/location/Green-E";
var mbtaStopsEndpoint = "/stops/line/Green-E";
function Map() { 
  const svgRef = useRef(null);
  const [stationData, setStationData] = useState(null);
  const [stationMapping, setStationMapping] = useState(null);

////////////////////////////////////////////////////////////
  var xhr = new XMLHttpRequest();
    // Making our connection 
  var url = "http://" + backendHost + controllerEndpoint + mbtaStopsEndpoint;
  xhr.open("GET", url, true);

    // function execute after request is successful
  xhr.onreadystatechange = function () {
    console.log("hi");
    if (this.readyState == 4 && this.status == 200) {
        var temp = JSON.parse(this.responseText);
        var stationDataTemp = Object.keys(temp).map(v => ({[v.replace(/['"]+/g, '')]: {...temp[v]}}));
        console.log(stationDataTemp);
        setStationData(stationDataTemp);     
    }
  }
  xhr.onerror = function (error){
      console.error(error);
    };
  xhr.send();
  console.log("sent #1");

///////////////////////////////////////////
  var xhr2 = new XMLHttpRequest();
    // Making our connection 
  var url2 = "http://" + backendHost + controllerEndpoint + mbtaStopsEndpoint;
  xhr2.open("GET", url, true);

    // function execute after request is successful
  xhr2.onreadystatechange = function () {
    console.log("hi");
    if (this.readyState == 4 && this.status == 200) {
        var temp = JSON.parse(this.responseText);
        console.log("mapping .. :", temp);
        setStationMapping(temp);      
    }
  }
  xhr2.onerror = function (error){
      console.error(error);
    };
  xhr2.send();
  console.log("sent #2");
/////////////////////////
  if(stationData == null || stationMapping == null){
    return(<div>Loading...</div>);
  }
  return (
    <div className="Map">
      <svg height="2000" width="2000" ref={svgRef} >
      <Draw stationData={stationData}/>
      <Vehicles svgRef={svgRef} stationData={stationData}/>
      </svg>
    </div> 
  );

}

export default Map;
