import { useState, useEffect } from "react";
import * as d3 from "d3";
import splitData from "./split_data.json";




var backendHost = "localhost:8080/";
var controllerEndpoint = "mbta/v1/livemap";
var vehiclePositionSub = "/vehicle/location/Red";
var mbtaStopsEndpoint = "/stops/line/Red"
function Map() {    console.log("#80");

    function createSVGMap(){
      addStops();
    }

    const[event, setEvent] = useState("hi");
    //Load stops first
    //Load lines next

    //Load vehicles below
    useEffect(() => {
      const evtSource = new EventSource("http://" + backendHost + controllerEndpoint + vehiclePositionSub);
      evtSource.onmessage = (data) => {
        setEvent(data.data);
      };
      evtSource.onerror = function (error) {
        console.error('EventSource error:', error);
      };
      //close out event subscription on close of component
    },[]);

      return (
        <div className="Map">
          <svg height="1000" width="10000"></svg>
          {createSVGMap()}
          <h1>Recent Event is {event}</h1>
        </div> 
      );
}


function addStops(){
  var xhr = new XMLHttpRequest();

    // Making our connection 
    var url = "http://" + backendHost + controllerEndpoint + mbtaStopsEndpoint;
    xhr.open("GET", url, true);

    // function execute after request is successful
    xhr.onreadystatechange = function () {
        if (this.readyState == 4 && this.status == 200) {
            console.log(JSON.parse(this.responseText));
            // this.responseText
            var temp = JSON.parse(this.responseText);
            var stationData = Object.keys(temp).map(v => ({[v.replace(/['"]+/g, '')]: {...temp[v]}}));

            console.log(stationData);
            var svg = d3.select("svg");
            var stopGroup = svg.selectAll("g")
            .data(stationData)
            .enter()
            .append("g")
            .attr("transform", function(d, i) { console.log(d); return "translate(" + (10 + i * 40) + ", " + (150) + ")"; });

            //To put in real time coordinates use below reference
            // .attr("transform", function(d, i) { console.log(d); return "translate(" + (250 - (((d[Object.keys(d)[0]].longitude - bostonBoundaries["easternBoundary"])/longitudeRange) * 250))  + ", " + (250 - (((d[Object.keys(d)[0]].latitude - bostonBoundaries["southernBoundary"])/latitudeRange) * 250)) + ")"; });

            stopGroup.append("circle")
              .attr("cx", 0)
              .attr("cy", 0)
              .attr("r", 2)
              .attr("fill", "red")
              .attr("class", d=>Object.keys(d)[0]);

             stopGroup.append("text")
                .text(function(d) {
                  console.log(d);
                  return d[Object.keys(d)[0]].name;})
                .attr("width", 100)
                .attr("x", function(d){
                  return 0;
                }) // Set the x-coordinate
                .attr("y", -10)
                .attr("text-anchor", "middle")
                .attr("font-size", 5);

                console.log("hellohi")
        }
      }
    xhr.onerror = function (error){
      console.error(error);
    };
    xhr.send();
    console.log("sent");
}
export default Map;
