import { useState, useEffect } from "react";
import * as d3 from "d3";



var backendHost = "localhost:8080/";
var controllerEndpoint = "mbta/v1/livemap";
var vehiclePositionSub = "/vehicle/location/Green-E";
var mbtaStopsEndpoint = "/stops/line/Green-E";

function Vehicles({ svgRef }) { 
    const [vehicleList, setVehicleList] = useState([]);

    useEffect(() => {
      const svg = svgRef.current;
      var svgElement = d3.select(svg);
       console.log("vdata ", vehicleList);
      svgElement.selectAll("text.vehicle")
      .data(vehicleList, d=>d.id)
      .join(
        (enter) => {
          enter
          .append("text")
          .attr("class", "vehicle")
          // .attr("key", d=>d.id)
          .attr("x", function(d,i){return 30 + i * 40})
          .attr("y", 300)
          .attr("font-size", 5)
          .text(d=>d.currentStatus);
      },
      (update) => {
        update.attr("x", function(d,i){return 30 + i * 40})
      },
      (exit) => {
        exit.remove(); 
    } 
      );
    }, [vehicleList, vehicleData]);

    //Load vehicles below
    useEffect(() => {
      const evtSource = new EventSource("http://" + backendHost + controllerEndpoint + vehiclePositionSub);
      evtSource.onmessage = (data) => {
        var eventData = JSON.parse(data.data);
        var vehicleData = eventData.vehicleData;
        if(eventData.event == "reset"){
          console.log("reset");
          setVehicleList(vehicleData);
        } else if(eventData.event == "update"){
          console.log(vehicleList);
          var newData = vehicleList.map(item => {
            console.log("map item .. ", item);
            if (item.id == vehicleData[0].id){
              return vehicleData[0];
            }
            else
              return item;
          });
          console.log("update", newData);
          setVehicleList(newData);
        }

      };
      evtSource.onerror = function (error) {
        console.error('EventSource error:', error);
      };
      //close out event subscription on close of component
      return () => {
        evtSource.close();
      };
    },[]);
}



export default Vehicles;
